SELECT REPLICATE(V.TxtChar, Occ.Occurences)
FROM (VALUES
('0',3,10),('1',3,12),('2',3,10),('3',3,10),('4',4,10),('5',3,10),('6',3,10),('7',3,10),('8',3,10),('9',3,10),('a',3,10)
