) AS V(TxtChar,MinOccurences,MaxOccurences)
INNER JOIN
(SELECT TOP (12) ROW_NUMBER() OVER (ORDER BY (SELECT NULL)) AS Occurences FROM sys.all_columns) AS Occ
ON Occ.Occurences BETWEEN V.MinOccurences AND V.MaxOccurences

UNION ALL

